import mongoose from 'mongoose';
const locationSchema = mongoose.Schema(
  {
    lc_id: {
      type: String,
      required: true,
    },
    name: {
      type: String,
      required: true,
    },
    address: {

      addressline1: String,
      addressline2: String,
      area: String,
      city: String,
      state: String,
    },
    pincode:{
      type : Number ,
      required: true,
    },
    
    annual_revenue: {
      type: Number,
      required: true,
    },
    ph_no: [Number],
  },
  {
    timestamps: true,
  }
);

export const Location = mongoose.model('Location', locationSchema);
